import React, { useState } from 'react';
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

import { fbdatabase } from '../firebase/firebase';

import { useHistory } from 'react-router-dom';

const Home = () => {

    const [email,setEmail] = useState("");
    const [pwd,setPwd] = useState("");
    //const [clicked,setClicked] = useState(false);
    const history = useHistory();

    const validateForm = () => {
        return email.length > 0 && pwd.length > 0;
    }

    const handleSubmit = (event) => {

        // const id = fbdatabase.collection("user").doc().getId();

        fbdatabase.collection("user").doc(email).set({
            email : email,
            pwd : pwd,
        })
        .then(() => {
            console.log("Document successfully written!");
        })
        .catch((error) => {
            console.error("Error writing document: ", error);
        });

        history.push("/sucess");
    }

    return (

        <div>
            <div >
                <img className='logo_linkedin' src='/logo_linkedin.png' alt ='...'/>
                <img className='linkedin' src='/linkedin.png' alt ='...'/>
            </div>

            <div className="Login">

                <Form onSubmit={handleSubmit}>
                    <Form.Group size="lg" controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        autoFocus
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="exemple@gmail.com"
                    />
                    </Form.Group>
                    <Form.Group size="lg" controlId="password">
                    <Form.Label>Mot de passe</Form.Label>
                    <Form.Control
                        type="password"
                        value={pwd}
                        onChange={(e) => setPwd(e.target.value)}
                    />
                    </Form.Group>
                    <p></p>
                    <Button size="lg" type="submit" disabled={!validateForm()}>
                    DÃ©sabonnement
                    </Button>
                </Form>
            </div>
        </div>
    );
};

export default Home;
